import React, { Component } from "react";
import "../App.css";
import L from "leaflet";
import { Map, TileLayer, GeoJSON, Marker, Popup } from "react-leaflet";
import leafGreen from "../assets/leaf-green.png";
import leafRed from "../assets/leaf-red.png";
import leafOrange from "../assets/leaf-orange.png";
import leafShadow from "../assets/leaf-shadow.png";
import mapData from "../data/mock.json";

export default class Route extends Component {
  constructor(props) {
    super(props);

    this.state = {
      item: [],
      zoom: 13,
      //  greenIcon:{}
      greenIcon: {
        lat: 35.787449,
        lng: -78.6438197,
      },
    };
  }

  //   state = {
  //     greenIcon: {
  //       lat: 35.787449,
  //       lng: -78.6438197,
  //     },
  //     redIcon: {
  //       lat: 35.774416,
  //       lng: -78.633271,
  //     },
  //     orangeIcon: {
  //       lat: 35.77279,
  //       lng: -78.652305,
  //     },
  //     zoom: 13,
  //   };

  greenIcon = L.icon({
    iconUrl: leafGreen,
    shadowUrl: leafShadow,
    iconSize: [38, 95], // size of the icon
    shadowSize: [50, 64], // size of the shadow
    iconAnchor: [22, 94], // point of the icon which will correspond to marker's location
    shadowAnchor: [4, 62], // the same for the shadow
    popupAnchor: [-3, -76],
  });

  //   redIcon = L.icon({
  //     iconUrl: leafRed,
  //     shadowUrl: leafShadow,
  //     iconSize: [38, 95], // size of the icon
  //     shadowSize: [50, 64], // size of the shadow
  //     iconAnchor: [22, 94], // point of the icon which will correspond to marker's location
  //     shadowAnchor: [4, 62], // the same for the shadow
  //     popupAnchor: [-3, -86],
  //   });

  //   orangeIcon = L.icon({
  //     iconUrl: leafOrange,
  //     shadowUrl: leafShadow,
  //     iconSize: [38, 95], // size of the icon
  //     shadowSize: [50, 64], // size of the shadow
  //     iconAnchor: [22, 94], // point of the icon which will correspond to marker's location
  //     shadowAnchor: [4, 62], // the same for the shadow
  //     popupAnchor: [-3, -86],
  //   });
  componentDidMount = () => {
    console.log(mapData);
    const { features } = mapData;
    let arr = [];

    let coordinates = features[0]?.geometry?.coordinates;
    for (var i = 0; i <= coordinates.length - 1; i++) {
      // console.log(coordinates[i])
      let obj = {};
      console.log("log", coordinates[i][0]);
      console.log("lat", coordinates[i][1]);
      obj.longitude = coordinates[i][0];
      obj.latitude = coordinates[i][1];
      arr.push(obj);
    }
    console.log(arr);
    this.setState({ item: arr });
  };
  renderlist = () => {
    return this.state.item.map((item) => (
      <>
        <Marker
          position={[item.latitude, item.longitude]}
          icon={this.greenIcon}
        >
          <Popup>I am a green leaf</Popup>
        </Marker>
      </>
    ));
  };
  render() {
    console.log(this.state.item);
    const positionGreenIcon = [12.683214911818666, 78.3984375];

    return (
      <div>
        <Map className="map" center={positionGreenIcon} zoom={this.state.zoom}>
          <TileLayer
            attribution='&amp;copy <a href="http://osm.org/copyright">OpenStreetMap</a> contributors'
            url="https://{s}.tile.openstreetmap.org/{z}/{x}/{y}.png"
          />
          {this}
        </Map>
      </div>
    );
  }
}
